$(document).ready(function(){
    $('.team-list').slick({
        slidesToShow: 3,
        slidesToScroll: 1,
        centerMode: true,
        centerPadding: '20px'
    });
});
$(document).ready(function(){
    $('.feedback-list').slick({
        slidesToShow: 1,
        slidesToScroll: 1
    });
});

if (document.querySelector('.signToHaircut__popup')) {
    const popupBg = document.querySelector('.popup-bg')
    const sign = document.querySelector('.signToHaircut__popup')
    const callBack = document.querySelector('.callBack__popup')

    document.querySelectorAll('.cta-button').forEach(ctaBtn => {
        ctaBtn.addEventListener('click', () => {
            sign.classList.toggle('popup__active')
            popupBg.classList.toggle('popup-bg__active')
            disableScroll()
        })
    })
    popupBg.addEventListener('click', () => {
        if(sign.classList.contains('popup__active')) {
            sign.classList.toggle('popup__active')
        } else {
            callBack.classList.toggle('popup__active')
        }
        popupBg.classList.toggle('popup-bg__active')
        disableScroll()
    })
    document.querySelector('.back-call').addEventListener('click', () => {
        callBack.classList.toggle('popup__active')
        popupBg.classList.toggle('popup-bg__active')
        disableScroll()
    })
}

let disableScroll = function() {
    let paddingOffset = window.innerWidth - document.body.offsetWidth + 'px'
    document.body.classList.toggle('scroll-lock')
    document.body.style.paddingRight = paddingOffset
}