$(document).ready(function(){
    $('.team-list').slick({
        slidesToShow: 3,
        slidesToScroll: 1,
        centerMode: true,
        centerPadding: '20px',
        responsive: [
            {
                breakpoint: 1281,
                settings: {
                    slidesToScroll: 1,
                    slidesToShow: 2,
                    centerMode: false
                }
            },
            {
                breakpoint: 769,
                settings: {
                    slidesToScroll: 1,
                    slidesToShow: 1,
                    centerMode: false
                }
            },
            {
                breakpoint: 421,
                settings: {
                    slidesToScroll: 1,
                    slidesToShow: 1,
                    centerMode: false,
                    arrows: false,
                    dots: true
                }
            }
        ]
    });
});
$(document).ready(function(){
    $('.feedback-list').slick({
        slidesToShow: 1,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 421,
                settings: {
                    arrows: false,
                    dots: true
                }
            }
        ]
    });
});

if (document.querySelector('.signToHaircut__popup')) {
    const popupBg = document.querySelector('.popup-bg')
    const sign = document.querySelector('.signToHaircut__popup')
    const callBack = document.querySelector('.callBack__popup')

    document.querySelectorAll('.cta-button').forEach(ctaBtn => {
        ctaBtn.addEventListener('click', () => {
            sign.classList.toggle('popup__active')
            popupBg.classList.toggle('popup-bg__active')
            disableScroll()
        })
    })
    popupBg.addEventListener('click', () => {
        if(sign.classList.contains('popup__active')) {
            sign.classList.toggle('popup__active')
        } else {
            callBack.classList.toggle('popup__active')
        }
        popupBg.classList.toggle('popup-bg__active')
        disableScroll()
    })
    document.querySelector('.back-call').addEventListener('click', () => {
        callBack.classList.toggle('popup__active')
        popupBg.classList.toggle('popup-bg__active')
        disableScroll()
    })
}

let disableScroll = function() {
    let paddingOffset = window.innerWidth - document.body.offsetWidth + 'px'
    document.body.classList.toggle('scroll-lock')
    document.body.style.paddingRight = paddingOffset
}

let burger = document.querySelector('.menu-icon').addEventListener('click', function() {
    disableScroll()
    let menu = document.querySelector('.menu')
    this.classList.toggle('menu-icon__active')
    menu.classList.toggle('menu__active')

    let links = document.querySelectorAll('.menu__item').forEach(element => {
        element.onclick = () => {
            menu.classList.toggle('menu__active')
            this.classList.toggle('menu-icon__active')
            disableScroll()
        }
    })
})